CREATE DEFINER=`root`@`localhost` PROCEDURE `InserirInscricao`(
  IN p_usuario_id INT,
  IN p_evento_id INT
)
BEGIN
  DECLARE inscricao_id INT;

  -- Verifica se já existe uma inscrição
  SELECT id INTO inscricao_id
  FROM inscricao
  WHERE usuario_id = p_usuario_id AND evento_id = p_evento_id
  LIMIT 1;

  IF inscricao_id IS NULL THEN
    -- Insere nova inscrição
    INSERT INTO inscricao (usuario_id, evento_id, criado_em)
    VALUES (p_usuario_id, p_evento_id, NOW());

    -- Pega o ID recém-criado
    SET inscricao_id = LAST_INSERT_ID();
  END IF;

  -- Retorna a linha da inscrição (nova ou existente)
  SELECT * FROM inscricao WHERE id = inscricao_id;
END ;;
