CREATE DEFINER=`root`@`localhost` PROCEDURE `inserir_ou_atualizar_avaliacao`(
  IN p_usuario_id INT,
  IN p_evento_id INT,
  IN p_nota INT
)
BEGIN
  DECLARE existe INT;

  -- Verifica se já existe avaliação desse usuário para o evento
  SELECT COUNT(*) INTO existe
  FROM avaliacao
  WHERE usuario_id = p_usuario_id AND evento_id = p_evento_id;

  IF existe > 0 THEN
    -- Atualiza a avaliação existente
    UPDATE avaliacao
    SET nota = p_nota,
        criado_em = NOW()
    WHERE usuario_id = p_usuario_id AND evento_id = p_evento_id;
  ELSE
    -- Insere nova avaliação
    INSERT INTO avaliacao (usuario_id, evento_id, nota, criado_em)
    VALUES (p_usuario_id, p_evento_id, p_nota, NOW());
  END IF;

  -- Atualiza a média no evento
  CALL atualizar_media_evento(p_evento_id);
END ;;
